            },
            {
              "key": "role",
              "value": "Promotes osteoclastogenesis and MMP expression"
            }
          ]
        },
        {
          "id": "MMP-9",
          "name": "MMP-9",
          "properties": [
            {
              "key": "type",
              "value": "Matrix Metalloproteinase"
            },
            {
              "key": "role",
              "value": "Degrades bone matrix components"
            }
          ]
        }
      ],
      "edges": [
        {
          "source_id": "Th17 cell pathway",
          "target_id": "IL-17",
          "relation_type": "secretes"
        },
        {
          "source_id": "Denosumab",
          "target_id": "MMP-9",
          "relation_type": "downregulates expression of"
